mapper.insert:
INSERT
  INTO TB_EXAM_MAIN_CATEGORY
     ( MAIN_CATEGORY_NAME
     , USE_STATUS
     , REG_DATE
     , REG_IDX
     )
VALUES
     ( #{mainQuestion.mainCategoryName}
     , 1
     , now()
     , #{user.idx}
     );

INSERT INTO TB_EXAM_SUB_CATEGORY(
MAIN_CATEGORY_IDX,
SUB_CATEGORY_NAME,
OPTIONS_CNT,
EXAM_CUT_OFF_SCORE,
USE_STATUS,
REG_DATE,
REG_IDX
)VALUES(
     #{subQuestion.mainCategoryIdx}
   , #{subQuestion.subCategoryName}
   , #{subQuestion.optionsCnt}
   , #{subQuestion.examCutOffScore}
   , 1
   , now()
   , #{user.idx}
);

INSERT INTO TB_EXAM_SUBJECT(
  SUB_CATEGORY_IDX
, SUBJECT_NAME
, USE_STATUS
, REG_DATE
, REG_IDX
, SUBJECT_CUT_OFF_SCORE
, SUBJECT_QUESTION_CNT
) VALUES(
  #{subject.subCategoryIdx}
, #{subject.subjectName}
, 1
, now()
, #{user.idx}
, #{subject.subjectCutOffScore}
, #{subject.subjectQuestionCnt}
);

mapper.select:
SELECT e.IDX, e.SUB_CATEGORY_NAME, e.USE_STATUS, date_format(e.REG_DATE, '%Y-%m-%d') AS regDate, e.REG_IDX, date_format(e.UDT_DATE, '%Y-%m-%d') AS udtDate, e.UDT_IDX, m.MAIN_CATEGORY_NAME, u.NAME as regUserName, IFNULL(user.NAME,"없음") as udtUserName FROM TB_EXAM_SUB_CATEGORY e LEFT JOIN TB_USER u ON REG_IDX = u.IDX LEFT JOIN TB_USER user ON UDT_IDX = user.IDX LEFT JOIN TB_EXAM_MAIN_CATEGORY m ON MAIN_CATEGORY_IDX = m.IDX WHERE
m.MAIN_CATEGORY_NAME LIKE CONCAT('%',#{searchObject},'%');
e.SUB_CATEGORY_NAME LIKE CONCAT('%',#{searchObject},'%');
m.MAIN_CATEGORY_NAME LIKE CONCAT('%',#{searchObject},'%') OR e.SUB_CATEGORY_NAME LIKE CONCAT('%',#{searchObject},'%');

mapper.insert:
INSERT
  INTO TB_EXAM
     ( MAIN_CATEGORY_IDX
     , SUB_CATEGORY_IDX
     , EXAM_NAME
     , REG_IDX
     , REG_DATE
     , CONFIRM_STATUS
     ) VALUES
     ( #{examInfo.mainCategoryIdx}
     , #{examInfo.subCategoryIdx}
     , #{examInfo.examName}
     , #{user.idx}
     , now()
     , 0
     )

INSERT
  INTO TB_EXAM_QUESTION
     ( EXAM_IDX
     , SUBJECT_IDX
     , MULTIMEDIA_IDX
     , QUESTION_NUMBER
     , QUESTION_NAME
     , ANSWER_NUMBER
     ) VALUES
     ( #{examInfo.examIdx}
     , #{question.subjectIdx}
     , #{question.multiMediaIdx}
     , #{question.questionNumber}
     , #{question.questionName}
     , #{question.answerNumber}
     )

INSERT
  INTO TB_EXAM_OPTIONS
     ( QUESTION_IDX
     , OPTIONS_NUMBER
     , OPTIONS_CONTENT
     ) VALUES

     ( #{question.questionIdx}
     , #{item.optionNumber}
     , #{item.optionContent}
     )

mapper.select:
SELECT OPTIONS_CNT
FROM TB_EXAM_SUB_CATEGORY
WHERE IDX = #{subIdx}

mapper.update:
UPDATE TB_EXAM
SET CONFIRM_STATUS = 1
WHERE IDX = #{examIdx};
